mapper.select:
SELECT vv.ID_,vv.visitor_time, ( SELECT count(*) from data_acquisition d LEFT JOIN questionnaire q ON q.ID_=d.data_question WHERE d.create_time = DATE_FORMAT(vv.visitor_time,'%Y-%m-%d') AND d.user_id=#{visitorId} ) flag, ( SELECT count(*) from visiting_record_file f WHERE f.deleted=0 AND f.creator=vv.creator AND f.record_id=vv.ID_ ) filesCount
FROM visiting_record vv WHERE vv.deleted=0 AND vv.visitor_id=#{visitorId}
ORDER BY vv.visitor_time DESC

SELECT vr2.visitor_name, vr2.phone ,vr.*,1 type FROM visiting_record vr LEFT JOIN visitor_register vr2 ON vr.visitor_id=vr2.ID_
WHERE vr.deleted=0 AND vr.creator=#{creator}
AND vr2.visitor_name LIKE '%${visitorName}%'

    SELECT b.* FROM (
    SELECT vr2.visitor_name, vr2.phone ,vr.ID_,vr.Visiting_purpose,vr.visitor_time,
					1 type,vr.visitor_id,vr.create_time FROM visiting_record vr
					LEFT JOIN visitor_register vr2 ON vr.visitor_id=vr2.ID_
					WHERE vr.deleted=0 AND vr.creator=#{creator}
    UNION ALL

    SELECT s.visitor_name,s.phone,s.ID_,  s.describes Visiting_purpose,s.visitor_time,
				2 type, s.visitor_id,s.create_time FROM schedule s
				WHERE s.deleted=0 AND s.consultant_id=#{consultantId}

    ) b WHERE  1=1


      AND b.visitor_name LIKE '%${visitorName}%'




      AND b.phone LIKE '%${phone}%'



      AND b.visitor_time >= #{start} AND b.visitor_time <= #{end}



      AND b.type=#{type}



      AND b.create_time >= #{start1} AND b.create_time <= #{end1}

select count(*)  from
  visiting_record
    where visitor_id =#{userId}
